import React, { useState } from 'react';
import '../styles/rPage.css';

export default function RecipePage({name, image, time, desc, ingredients, instructions}) {
    const [newIngredient, setNewIngredient] = useState('');

    function inputHandler(e) {
        setNewIngredient()
    }    
    function handleSubmit(e) {
        e.preventDefault();
        setNewIngredient("");
    }


    return (
        <div className='rec max-width'>
            <div className='recipe'>
                <div className="recipeLeft">
                    <h1>{name}</h1>
                    <p id='time'>{time}</p>
                    <p className='description'>{desc}</p>
                </div>
                <div className="recipeRight">
                    <img src={image} alt={name}/>
                </div>
            </div>
            <div className="recipe-panel">
            <div className="ingredients">
                <h3>Ingredients</h3>
                <ul>
                    {ingredients.map((ingred) => {
                        return(<li>{ingred}</li>);
                    })}
                    {newIngredient}
                </ul>
            </div>
            <div className="instructions">
                <h3>Instructions</h3>
                <ol>
                    {instructions.map((step) => {
                        return(<li>{step}</li>);
                    })}
                </ol>
            </div>
            </div>

            <div className="add-ingredient">
                <form className="ingredient-form" onSubmit={handleSubmit}>
                <input 
                    className="ingredient-input"
                    id = "ingred" 
                    type="text"
                    placeholder="Add things you need here!" 
                    />
                <button type="submit" className="submit-button">add</button>
                </form>
            </div>
        </div>
    )
}
